-- Set up the year. Begin Try Drop Table #year End Try Begin Catch End Catch Create Table #year (year_str VARCHAR(4)) Begin Try Drop Table #day End Try Begin Catch End Catch Create Table #day (day_int SMALLINT) DECLARE @i SMALLINT
Insert #year Select Cast(YearNum as varchar(4)) From #tblDimDate;
-- Set up a table of day-of-month values. Set @i = 1; WHILE @i < 32 BEGIN INSERT #day VALUES(@i) Set @i = @i + 1; END
-- Union all the holidays together. Insert Into #tblHolidays (HolidayDesc, HolidayYear, HolidayDate) SELECT 'New Years' AS Holiday, year_str AS [Year], CASE WHEN DATEPART(dw,CONVERT(Date,'01/01/'+year_str)) = 1 THEN DATEADD(dd,1,CONVERT(Date,'01/01/'+year_str)) WHEN DATEPART(dw,CONVERT(Date,'01/01/'+year_str)) = 7 THEN DATEADD(dd,-1,CONVERT(Date,'01/01/'+year_str)) ELSE CONVERT(Date,'01/01/'+year_str) END AS [Date] FROM #year UNION -- Lee-Jackson-King Day: Third Monday in January: -- (MAX) WHERE dw = 2 and day_int < 22 SELECT 'Lee-Jackson-King', year_str, MAX(CONVERT(Date,'01/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) FROM #day,#year WHERE DATEPART(dw,CONVERT(Date,'01/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) = 2 AND day_int < 22 GROUP BY year_str UNION -- Memorial Day: Last Monday in May: (MAX) WHERE dw = 2 SELECT 'Memorial Day', year_str, MAX(CONVERT(Date,'05/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) FROM #day,#year WHERE DATEPART(dw,CONVERT(Date,'05/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) = 2 GROUP BY year_str UNION -- SELECT 'Fourth Of July', year_str, CASE WHEN DATEPART(dw,CONVERT(Date,'07/04/'+year_str)) = 1 THEN CONVERT(Date,'07/05/'+year_str) WHEN DATEPART(dw,CONVERT(Date,'07/04/'+year_str)) = 7 THEN CONVERT(Date,'07/03/'+year_str) ELSE CONVERT(Date,'07/04/'+year_str) END FROM #year UNION -- Labor Day: First Monday in September: MIN WHERE dw = 2 -- Keep day_int less than 31 because 9/31 is an out-of-range Date value. SELECT 'Labor Day', year_str, MIN(CONVERT(Date,'09/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) FROM #day,#year WHERE DATEPART(dw,CONVERT(Date,'09/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) = 2 AND day_int < 31 GROUP BY year_str UNION
-- Thanksgiving: Fourth Thursday in November: MAX WHERE dw = 5 and day_int < 29 SELECT 'Thanksgiving', year_str, MAX(CONVERT(Date,'11/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) FROM #day,#year WHERE DATEPART(dw,CONVERT(Date,'11/'+CONVERT(VARCHAR(2),day_int)+'/'+year_str)) = 5 AND day_int < 29 GROUP BY year_str
UNION SELECT 'Christmas', year_str, CASE WHEN DATEPART(dw,CONVERT(Date,'12/25/'+year_str)) = 1 THEN CONVERT(Date,'12/26/'+year_str) WHEN DATEPART(dw,CONVERT(Date,'12/25/'+year_str)) = 7 THEN CONVERT(Date,'12/24/'+year_str) ELSE CONVERT(Date,'12/25/'+year_str) END FROM #year --Take the Holidays just created and updated the temporary data structure with the dates Update #tblDimDate Set IsFederalUSHoliday = 'Y', HolidayDescription = HolidayDesc From #tblHolidays H Where #tblDimDate.FullDate = H.HolidayDate --*********************************************************************************************************************** --20. Return the data in temporary Date structure --*********************************************************************************************************************** If @pi_tiResultsType = 1 --Regular Result Set Begin Set @nvcSQL = 'SELECT ' + @pi_vcColumnList + ' FROM #tblDimDate order by DateKey;'; Execute sp_executesql @nvcSQL End Else If @pi_tiResultsType = 3 --Create an flat file for data import. the data is returned to the consumer --where the dataset is saved to a file. Begin Select '"' + Cast( DateKey as varchar(100) ) + '"' + Case When CHARINDEX( ',DateType,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DateType as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',FullDate,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( FullDate as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',YearNum,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( YearNum as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',YearBeginDate,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( YearBeginDate as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',YearEndDate,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( YearEndDate as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayNumOfWeek,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayNumOfWeek as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayNumOfMonth,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayNumOfMonth as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayNumOfQuarter,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayNumOfQuarter as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayNumOfYear,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayNumOfYear as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayOfWeekName,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayOfWeekName as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',DayOfWeekAbbreviation,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( DayOfWeekAbbreviation as varchar(100) ) + '"' Else '' End + Case When CHARINDEX( ',JulianDayNumOfYear,', @vcColumnListForComparison,1 ) > 0 Then ',"' + Cast( JulianDayNumOfYear as varchar(100) ) + '"' Else '' End +
