EXEC [dbo].[CreateDimensionUnknownRow] @TableName = DimCustomerLocation, @TableSchema = harsco, @Action = N'1'
--truncate table Harsco.dimCustomerLocation begin tran MERGE Harsco.DimCustomerLocation AS T USING (
sELECT A.ACCOUNT_NUMBER,c.location, dc.customerkey, C.site_use_id, l.address1, address2, address3, city, state, l.country, postal_code FROM stg.oracle_hz_cust_accounts A inner join stg.oracle_HZ_CUST_ACCT_SITES_ALL B on A.CUST_ACCOUNT_ID = B.CUST_ACCOUNT_ID inner join stg.oracle_HZ_CUST_SITE_USES_ALL C on B.CUST_ACCT_SITE_ID = C.CUST_ACCT_SITE_ID inner join stg.oracle_HZ_PARTY_SITES P on B.PARTY_SITE_ID = P.PARTY_SITE_ID inner join stg.oracle_HZ_LOCATIONS L on P.LOCATION_ID = L.LOCATION_ID inner join harsco.dimcustomer dc
on A.ACCOUNT_NUMBER = dc.CustomerAccountNumber and dc.sourcesystemid = 27
) AS S ON (T.CustomerLocationNumber = Convert(nvarchar(max),S.Site_use_id)) WHEN NOT MATCHED BY TARGET THEN INSERT( [CustomerLocationNumber] ,customerkey ,[CustomerLocationName] ,[Address1] ,[Address2] ,[Address3] ,[City] ,[State] ,[PostalCode] ,[Province] ,[Country] ,[CreatedDateTime] ,[LastUpdatedDateTime] ,[EffectiveStartDate] ,[EffectiveEndDate] ,[CurrentRecordIndicator] ) values (Convert(nvarchar(max),S.Site_use_id), s.customerkey, s.location, s.address1, s.address2, s.address3, s.city, s.state, s.postal_code, s.country, s.country --start audit columns ,getdate(), getdate(), '2099-01-01', '2099-01-01', 1) WHEN MATCHED THEN UPDATE SET t.customerlocationnumber = s.site_use_id ,t.customerkey = s.customerkey ,t.[CustomerLocationName] = s.location ,t.[Address1] = s.address1 ,t.[Address2] = s.address2 ,t.[Address3] = s.address3 ,t.[City] = s.city ,t.[State] = s.state ,t.[PostalCode] = s.postal_code ,t.[Province] = s.country ,t.[Country] = s.country ;
commit tran

/******************************************************************************************************************* ** Procedure Name: MergeDimPaymentTerms ** Desc: ** Auth: Gene Furibondo ** Date: 09/24/2017 ******************************************************************************************************************** ** Change History -------------------------------------------------------------------------------------------------------------------- ** PR Date Author Description ** -- -------- ------- ------------------------------------ ** 1 09/27/2017 Dhilasu Reddy Added Inline Comments ********************************************************************************************************************/
--*********************************************************************************************************************** --1. Calling the 'CreateDimensionUnknownRow' stored procedure to ensure that a -1 record is present --***********************************************************************************************************************
EXEC [dbo].[CreateDimensionUnknownRow] @TableName = DimPaymentTerms, @TableSchema = harsco, @Action = N'1'
--*********************************************************************************************************************** --2. Source - Oracle -- Merge and Insert the Data where Source and Target data are not Matched , -- Merge and update the Data where Source and Target data are Matched --*********************************************************************************************************************** BEGIN TRAN; MERGE Harsco.DimPaymentTerms AS T USING ( select [TERM_ID], [NAME], [DESCRIPTION], cast(cast(cast(Due_Days as nvarchar(100)) as numeric(18,0)) as int) as Due_Days, sourcesystemid from [stg].[Oracle_RA_Terms] ) AS S ON (T.PaymentTermsID = Convert(nvarchar(max),S.Term_ID )and t.sourcesystemid = s.sourcesystemid) WHEN NOT MATCHED BY TARGET THEN INSERT( [PaymentTermsID] ,[PaymentTermsDays] ,[PaymentTermsDescription] ,[PaymentTermsName] ,SourceSystemID ,[CreatedDateTime] ,[LastUpdatedDateTime] ,[EffectiveStartDate] ,[EffectiveEndDate] ,[CurrentRecordIndicator] ) values (Convert(nvarchar(max),S.Term_ID ), [Due_Days], [DESCRIPTION], [NAME], SourceSystemID --start audit columns ,getdate(), getdate(), '2099-01-01', '2099-01-01', 1) WHEN MATCHED AND t.sourcesystemID =S.sourcesystemID THEN UPDATE SET t.PaymentTermsID = Convert(nvarchar(max),S.Term_ID ) -- no sourcesystemID ,t.[PaymentTermsDays] = [Due_Days] ,t.[PaymentTermsDescription] = [DESCRIPTION] ,t.[PaymentTermsName] = [NAME] ; ;
;
commit tran
--*********************************************************************************************************************** --2. Source - JDEdwards -- Merge and Insert the Data where Source and Target data are not Matched , -- Merge and update the Data where Source and Target data are Matched --***********************************************************************************************************************
BEGIN TRAN; MERGE Harsco.DimPaymentTerms AS T USING ( select PNPTC, PNPTD, cast(PNNDTP as int) as PNNDTP, d_sourcesystemid from [stg].[JDE_F0014] where rtrim(ltrim(pnptc)) is not null ) AS S ON (T.PaymentTermsID = S.PNPTC AND t.sourcesystemID = S.d_sourcesystemID) WHEN NOT MATCHED BY TARGET THEN INSERT( [PaymentTermsID] ,[PaymentTermsDays] ,[PaymentTermsDescription] ,[PaymentTermsName] ,SourceSystemID ,[CreatedDateTime] ,[LastUpdatedDateTime] ,[EffectiveStartDate] ,[EffectiveEndDate] ,[CurrentRecordIndicator] ) values (Convert(nvarchar(max),PNPTC),PNNDTP, PNPTD, PNPTD, d_sourcesystemid --start audit columns ,getdate(), getdate(), '2099-01-01', '2099-01-01', 1) WHEN MATCHED AND t.sourcesystemID = S.d_sourcesystemID THEN UPDATE SET t.PaymentTermsID = Convert(nvarchar(max),S.PNPTC) ,t.[PaymentTermsDays] = PNNDTP ,t.[PaymentTermsDescription] = PNPTD ,t.[PaymentTermsName] = PNPTD ;
;
commit tran

