create view Harsco.vLKPExchangeRateToday
as

SELECT DISTINCT [DateKey] ,[FromCurrency] ,[ToCurrency] ,[ExchangeRate]
FROM [Harsco].[LKPExchangeRate] EX WHERE ToCurrency='USD' AND ( DateKey like '2017%' OR DateKey like '2018%' OR DateKey like '2019%' OR DateKey like '202%' )
